.threshold-value-digit{
    pointer-events: none;
}

.value-digit-btn__up, .value-digit-btn__down{
    cursor: pointer;
}

.value-digit-btn__up.disabled-digit, .value-digit-btn__down.disabled-digit{
    cursor: default;
}
.value-digit-btn__up.disabled-digit rect, .value-digit-btn__down.disabled-digit rect{
    fill: #b0b0b0;
    stroke: #525252;
}

.value-digit-btn__up.disabled-digit polygon,
.value-digit-btn__down.disabled-digit polygon{
    fill: #525252;
}

.svg-text{
    pointer-events: none;
}

#save-button{
    cursor: pointer;
}
#save-button rect{
    fill: white;
}
#save-icon{
    cursor: pointer;
}

descriptor.controllerScript:
let settings;

self.onInit = function() {
    const ctx = self.ctx;
    const $scope = ctx.$scope;

    settings = [
        {
            sensor: "BOILER",
            attrKey: "boilerTempThresh",
            max: 800,
            min: 0,
            digits:{
                THOUSANDS: 0,
                HUNDREDS: 0,
                TENS: 0,
                ONES: 0
            }
        },
        {
            sensor: "MAIN_ENGINE",
            attrKey: "mainEngTempThresh",
            max: 800,
            min: 0,
            digits:{
                THOUSANDS: 0,
                HUNDREDS: 0,
                TENS: 0,
                ONES: 0
            }
        },
        {
            sensor: "LUBRICANT_OIL",
            attrKey: "lubricantOilTempThresh",
            max: 1400,
            min: 0,
            digits:{
                THOUSANDS: 0,
                HUNDREDS: 0,
                TENS: 0,
                ONES: 0
            }
        },
    ];

    const deviceId = {
        id: ctx.defaultSubscription.targetDeviceId,
        entityType: "DEVICE"
    };

    const digitsToInteger = function(digits){
        return  digits.THOUSANDS * 1000 +
                digits.HUNDREDS * 100 +
                digits.TENS * 10 +
                digits.ONES;
    };

    const integerToDigits = function(num, max, min){
        if(num > max || num < min || num > 9999){
            ctx.showErrorToast("Threshold value is out of limit!")
            return {
                THOUSANDS: 0,
                HUNDREDS: 0,
                TENS: 0,
                ONES: 0
            };
        }else{
            const numStr = num.toString();
            const strLength = numStr.length;
            return {
                THOUSANDS: strLength > 3? parseInt(numStr[strLength - 4]) : 0,
                HUNDREDS: strLength > 2? parseInt(numStr[strLength - 3]) : 0,
                TENS: strLength > 1? parseInt(numStr[strLength - 2]) : 0,
                ONES: strLength === 1? parseInt(numStr[0]) : 0
            };
        }
    };

    const getDigitElement = function(sensor, digit){
        return $(".threshold-value-digit").filter(function(_, element){
            return $(element).data("sensor") === sensor && $(element).data("digit") === digit;
        });
    }

    const getDigitButtons = function(sensor, digit){

        return{
            incrementBtn: $(".value-digit-btn__up").filter(function(_, element){
                return $(element).data("sensor") === sensor && $(element).data("digit") === digit;
            }),
            decrementBtn: $(".value-digit-btn__down").filter(function(_, element){
                return $(element).data("sensor") === sensor && $(element).data("digit") === digit;
            })
        }
    };

    const thresholdAttKeys = settings.map(function(setting){
        return setting.attrKey;
    });

    const setDisabledDigitStyle = function(incrementBtn, decrementBtn, digitElement){
        incrementBtn.addClass("disabled-digit");
        decrementBtn.addClass("disabled-digit");
        digitElement.css({fill: "#b0b0b0"});
    };

    ctx.attributeService.getEntityAttributes(deviceId, "SERVER_SCOPE", thresholdAttKeys).subscribe(function(attributes){
        console.log(attributes);

        settings.forEach(function(setting){
            const attribute =  attributes.find(function(attr){
               return attr.key === setting.attrKey;
            });

            if(attribute){
                setting.digits = integerToDigits(attribute.value, setting.max, setting.min);
            }

            // console.table(setting.digits);

            Object.keys(setting.digits).forEach(function(digit){
                const { sensor, max, digits } = setting;
                const digitValue = digits[digit];

                const digitElement = getDigitElement(sensor, digit);  
                digitElement.text(digitValue);

                const { incrementBtn, decrementBtn } = getDigitButtons(sensor, digit);

                if(digitValue === 0) decrementBtn.addClass("disabled-digit");
                if(digitValue === 9) incrementBtn.addClass("disabled-digit");

                if(max < 10  && (digit === "THOUSANDS" || digit === "HUNDREDS"|| digit === "TENS")){
                    setDisabledDigitStyle(incrementBtn, decrementBtn, digitElement);
                }else if(max < 100 && (digit === "THOUSANDS" || digit === "HUNDREDS")){
                    setDisabledDigitStyle(incrementBtn, decrementBtn, digitElement);
                }else if (max < 1000 && (digit === "THOUSANDS")){
                    setDisabledDigitStyle(incrementBtn, decrementBtn, digitElement);
                }

            });

        });

    });

    $scope.incrementDigit = function($event){
        // console.log($event);
        let btnGroup;
        const target = $event.target;

        if(target.tagName === "rect" || target.tagName === "polygon")
            btnGroup = target.parentElement;
        else
            btnGroup = target;

        const disabled = $(btnGroup).hasClass("disabled-digit");
        if(disabled) return;

        const sensor = $(btnGroup).data("sensor");
        const digit = $(btnGroup).data("digit");

        const temp = settings.find(function(setting){
            return setting.sensor === sensor;
        });

        if(temp){
            const {incrementBtn, decrementBtn} = getDigitButtons(sensor, digit);
            let digitValue = temp.digits[digit];

            if(digitValue === 9){
                digitValue = 9;
            }
            else{
                digitValue++;

                if(digitValue === 9 && !incrementBtn.hasClass("disabled-digit"))
                    incrementBtn.addClass("disabled-digit");

                if(digitValue === 1 && decrementBtn.hasClass("disabled-digit"))
                    decrementBtn.removeClass("disabled-digit");
                // if(incrementBtn.hasClass("disabled-digit"))
                //     incrementBtn.removeClass("disabled-digit");
            }

            const digitElement = getDigitElement(sensor, digit);

            digitElement.text(digitValue);

            temp.digits[digit] = digitValue;
            // console.log(digitElement)
        }

        // console.table(temp.digits);
    };

    $scope.decrementDigit = function($event){
        let btnGroup;
        const target = $event.target;

        if(target.tagName === "rect" || target.tagName === "polygon")
            btnGroup = target.parentElement;
        else
            btnGroup = target;

        const sensor = $(btnGroup).data("sensor");
        const digit = $(btnGroup).data("digit");

        const temp = settings.find(function(setting){
            return setting.sensor === sensor;
        });

        if(temp){
            const {incrementBtn, decrementBtn} = getDigitButtons(sensor, digit);
            let digitValue = temp.digits[digit];

            if(digitValue === 0)
                digitValue = 0;
            else{
                digitValue--;

                if(digitValue === 8 && incrementBtn.hasClass("disabled-digit"))
                    incrementBtn.removeClass("disabled-digit");

                if(digitValue === 0 && !decrementBtn.hasClass("disabled-digit"))
                    decrementBtn.addClass("disabled-digit");
            }

            const digitElement = getDigitElement(sensor, digit);

            digitElement.text(digitValue);

            temp.digits[digit] = digitValue;
            // console.log(digitElement);
        }

        // console.table(temp.digits);
    };

    $scope.save = function(){

        const isThresholdInLimits = settings.every(function(setting){
            const threshold = digitsToInteger(setting.digits);
            return threshold <= setting.max && threshold >= setting.min;
        });

        if(isThresholdInLimits){
            const attributes = settings.map(function(setting){
                return {
                    key: setting.attrKey,
                    value: digitsToInteger(setting.digits)
                };
            });

            console.log("attributes", attributes);

            ctx.attributeService.saveEntityAttributes(deviceId, "SERVER_SCOPE", attributes).subscribe(function(){
                ctx.showSuccessToast("Alarm thresholds saved!");

                const params = {
                    // entityName,
                    // entityLabel,
                    // // targetEntityParamName: string, 
                    // entityId
                };
                ctx.stateController.updateState("asyad_sensor_readings", params);
            });
        }else{
            ctx.showErrorToast("Some threshold values are out of limits!");
        }
    };

};


self.onResize = function() {};

self.onDestroy = function() {};
