class BarGauge {
  constructor(canvas, isVertical, options) {
    if (isVertical === undefined || isVertical === null) this.isVertical = true;
    else this.isVertical = isVertical;

    try {
      if (!canvas) throw new Error("Container is required!");

      this.canvas = canvas;
      if (isVertical) {
        if (this.canvas.width === 0) this.canvas.width = 300;
        if (this.canvas.height === 0) this.canvas.height = 150;
      } else {
        if (this.canvas.width === 0) this.canvas.width = 300;
        if (this.canvas.height === 0) this.canvas.height = 150;
      }

      this.canvas.style.width = 300;
      this.canvas.style.height = 150;

      if (!options) {
        options = this.getDefaults();
      }

      this.animId = null;
      this.value = 0;
      this.dv = 0; // Change in value (used for animation)
      this.options = options;
      this.unit = options.unit;
      this.ctx = this.canvas.getContext("2d");
    //   console.log("canvas", canvas, "ctx", this.ctx);
      this.render(this.options.value); //50
    } catch (error) {
      console.log(error);
    }
  }

  getDefaults() {
    return {
      min: 0,
      max: 100,
      value: 50,
      threshold: null,
      unit: "Â°C",
      barBorderColor: "#CEE9B6",
      barLength: this.isVertical ? this.canvas.height : this.canvas.width,
      barBreadth: 30,
      barFillColor: "#DBDBDB",
      textColor: "white",
      showLimits: false,
    };
  }

  setValue(value) {
    if(value > this.options.max) value = this.options.max;
    if(value < this.options.min) value = this.options.min;

    this.render(value);
    this.value = value;
    this.options.value = value;
  }

  setUnit(unit) {
    this.unit = unit;
    this.options.unit = unit;

    this.render(this.value);
  }

  setMin(min) {
    this.options.min = min;

    this.render(this.value);
  }

  setMax(max) {
    this.options.max = max;

    this.render(this.value);
  }

  setThreshold(threshValue) {
    this.options.threshold = threshValue;

    this.render(this.value);
  }

  setBarLength(length) {
    this.options.barLength = length;

    if (this.isVertical)
      this.barElementContainer.css({ height: length + "px" });
    else this.barElementContainer.css({ width: length + "px" });

    this.render();
  }

  _calculateValuePercentage(value) {
    var min = this.options.min;
    var max = this.options.max;
    try {
      if (min > max) throw new Error("Min limit cannot larger than max!");

      if (value > max || value < min) {
        this.valueOutOfLimit = true;
        throw new Error(`Value ${value} is out of limit!`);
      }

      this.valueOutOfLimit = false;

      return (value - min) / (max - min);
    } catch (error) {
      console.log(error);
      return null;
    }
  }

  _calculateValueChangePercentage() {
    var min = this.options.min;
    var max = this.options.max;
    try {
      if (min > max) throw new Error("Min limit has to be larger than max!");

      if (this.dv > max || this.dv < min) {
        this.valueOutOfLimit = true;
        throw new Error(`Value ${this.dv} is out of limit!`);
      }

      this.valueOutOfLimit = false;

      return (this.dv - min) / (max - min);
    } catch (error) {
      console.log(error);
      return null;
    }
  }

  _renderThresholdIndicator(x, y, w, h) {
    var tipX, tipY, triEdge1X, triEdge1Y, triEdge2X, triEdge2Y;
    var threshPercent = this._calculateValuePercentage(this.options.threshold);

    this.ctx.beginPath();
    this.ctx.strokeStyle = "#111217";
    this.ctx.lineWidth = 3;
    if(this.isVertical){
        tipX = x;
        tipY = Math.round((1 - threshPercent) * this.options.barLength);
    }else{
        tipX = Math.round(w - (1 - threshPercent) * this.options.barLength);
        tipY = y;
    }
    this.ctx.moveTo(tipX, tipY);
    if(this.isVertical) this.ctx.lineTo(tipX + this.options.barBreadth, tipY);
    else this.ctx.lineTo(tipX, tipY + this.options.barBreadth);

    this.ctx.stroke();
    // this.ctx.fillStyle = this.options.textColor;

    // var triBase = 15,
    //   triHeight = 10;

    // if (this.isVertical) {
    //   tipX = x + w + 2;
    //   tipY = Math.round((1 - threshPercent) * this.options.barLength);

    //   triEdge1X = tipX + triHeight;
