    // console.table({ threshold: this.options.threshold, tipX, tipY });
    // this.ctx.moveTo(tipX, tipY);
    // // this.ctx.lineTo(tipX, tipY + triHeight);

    // this.ctx.lineTo(triEdge1X, triEdge1Y);
    // this.ctx.lineTo(triEdge2X, triEdge2Y);
    // this.ctx.fill();
  }

  _renderValues(value, x, y, w, h) {
    this.ctx.beginPath();
    this.ctx.fillStyle = this.options.textColor;

    var valX, valY, maxX, maxY, minX, minY;

    if (this.isVertical) {
      valX = x - 70;
      valY = this.canvas.height / 2 + 6;
      maxX = x + w + 2;
      maxY = 16;
      minX = x + w + 2;
      minY = this.options.barLength;
    } else {
      valX = this.options.barLength / 2 - 20;
      valY = y - 6;
      maxX = this.options.barLength - 35;
      maxY = y + this.options.barBreadth + 16;
      minX = 0;
      minY = y + this.options.barBreadth + 16;
    }
    this.ctx.font = "24px Tajawal";
    this.ctx.fillText(value + this.unit, valX, valY);

    if (this.options.showLimits) {
      this.ctx.font = "16px Tajawal";
      this.ctx.fillText(this.options.max, maxX, maxY);
      this.ctx.fillText(this.options.min, minX, minY);
    }
  }

  _renderBar(x, y, w, h) {
    var startX, endX, startY, endY;
    if (this.isVertical) {
      startX = x;
      startY = this.canvas.height;

      endX = startX;
      endY = this.canvas.height - this.options.barLength;
    } else {
      startX = x;
      startY = y + this.options.barBreadth / 2;

      endX = x + this.options.barLength;
      endY = startY;
    }

    var barX, barW, barY, barH;
    var percent = this._calculateValueChangePercentage();

    if (this.isVertical) {
      barY = Math.round(y + (1 - percent) * this.options.barLength);
      barH = Math.round(h - (1 - percent) * this.options.barLength);
    } else {
      barX = 0;
      barW = Math.round(w - (1 - percent) * this.options.barLength);
    }
    //   console.table({
    //     percent,
    //     isIncrementing,
    //     newValue: value,
    //     oldValue: this.value,
    //     barLength: this.dh,
    //     barX,
    //     barW,
    //     x,
    //     w,
    //     barY,
    //     barH,
    //     y,
    //     h,
    //   });
    var grdFill = this.ctx.createLinearGradient(startX, startY, endX, endY);
    var threshPercent = this._calculateValuePercentage(this.options.threshold);
    grdFill.addColorStop(0, "#8ADB42");
    grdFill.addColorStop(threshPercent, "#8ADB42");
    grdFill.addColorStop(threshPercent, "#FD5247");

    // // Gradient
    // grdFill.addColorStop(0, "#8ADB42");
    // grdFill.addColorStop(0.45, "#FFEF5C");
    // grdFill.addColorStop(1, "#FD5247");

    this.ctx.beginPath();
    this.ctx.fillStyle = grdFill;

    if (this.isVertical) {
      this.ctx.fillRect(x, barY, w, barH);
    } else {
      this.ctx.fillRect(barX, y, barW, h);
    }
  }

  render(value) {
    if (this.animId !== undefined && this.animId !== null)
      cancelAnimationFrame(this.animId);
    // console.log("hello", this.options.barLength)
    var x, y, w, h;
    var velocity = 3;

    if (this.isVertical) {
      x = this.canvas.width / 2 - this.options.barBreadth / 2;
      y = this.canvas.height - this.options.barLength;
      w = this.options.barBreadth;
      h = this.options.barLength;
    } else {
      x = 0;
      y = this.canvas.height / 2 - this.options.barBreadth / 2;
      w = this.options.barLength;
      h = this.options.barBreadth;
    }

    var isIncrementing = this.value < value || this.value === value;
    var mod = isIncrementing ? 1 : -1;

    var animate = function (instance) {
      instance.ctx.clearRect(
        0,
        0,
        instance.canvas.width,
        instance.canvas.height
      );

      // Render value, min, and max texts
      instance._renderValues(value, x, y, w, h);

      // Render bar container
      instance.ctx.beginPath();
      instance.ctx.fillStyle = instance.options.barFillColor;
      instance.ctx.fillRect(x, y, w, h);

      // Render bar
      instance._renderBar(x, y, w, h);

      // Render threshold
      instance._renderThresholdIndicator(x, y, w, h);

      if (isIncrementing) {
        if (instance.dv >= value) return;
      } else {
        if (instance.dv <= value) return;
      }

      var nextDv = instance.dv + velocity * mod;

      if (isIncrementing) {
        // Set the value change to the target value
        if (nextDv > value) instance.dv = value;
        else instance.dv = nextDv;
      } else {
        if (nextDv < instance.min) instance.dv = instance.min;
        else instance.dv = nextDv;
      }

      instance.animId = requestAnimationFrame(() => animate(instance));
    };

    animate(this);
  }

  destroy() {
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

    this.canvas.width = 0;
    this.canvas.height = 0;
  }
}

