var onDataUpdated = function(subscription, detectChanges) {
    if ($scope.isEditMode) return;

    var subscData = subscription.data;

    // console.log("onData updated");
    // console.log("onDataUpdated", subscription);
    if (subscData.length < 1) return;

    subscData.forEach(function(keyData) {
        if (!(keyData && keyData.data && keyData.data[0])) return;

        var updatedDataKey = keyData.dataKey;

        var pinToUpdate = $scope.pins.find(function(pin) {
            return pin.telemetryKey === updatedDataKey.name;
        });

        var pinIndex = $scope.pins.findIndex(function(pin) {
            return pin.telemetryKey === updatedDataKey.name;
        });

        var pinValueElementToUpdate = $(".pin-card-radGaugeValue").eq(pinIndex);

        var newValue = keyData.data[0][1];

        // console.table({label:pinToUpdate.label, pinIndex, newValue: newValue});

        var pinObj = $scope.pins[pinIndex];

        if(pinObj.type === "RADIAL")
            pinObj.gaugeInstance.set(newValue);
        else
            pinObj.gaugeInstance.setValue(newValue);

    })
}

var onDataUpdateError = function(subscription, e) {
    console.log("onDataUpdatedError", e);
}

var createTelemetryKeysSubscription = function(){
    var subscriptionOptions = {
        callbacks: {
            onDataUpdated: (subscription, detectChanges) => ctx.ngZone.run(() => {
                onDataUpdated(subscription, detectChanges);
            }),
            onDataUpdateError: (subscription, e) => ctx.ngZone.run(() => {
                onDataUpdateError(subscription, e);
            }),
            dataLoading: () => {}
        }
    };

    var subscriptionsInfo = [{
        type: "entity",
        entityType: "DEVICE",
        entityId: ctx.defaultSubscription.targetDeviceId
    }];

    subscriptionsInfo[0].timeseries = $scope.pins.map(function(pin) {
        return {
            name: pin.telemetryKey
        };
    });

    ctx.subscriptionApi.createSubscriptionFromInfo("latest", subscriptionsInfo, subscriptionOptions, false, true).subscribe((subscription) => {
        // console.log("subscription", subscription);
        telemetrySubscription = subscription;

        // Update 'analogPinsConfigName' attribute
        ctx.attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", [{
            key: settings.analogPinsConfigName,
            value: $scope.pins.map(function(pin){
                return {
                    label: pin.label,
                    telemetryKey: pin.telemetryKey,
                    type: pin.type,
                    number: pin.number,
                    nodeId: pin.nodeId,
                    min: pin.min,
                    max: pin.max,
                    unit: pin.unit,
                    threshold: pin.threshold
                }
            })
        }]).subscribe(function() {
            // console.log("<-------ATTRIBUTE SAVED------->");
        });
    });
};

