                    <input type="text" matInput formControlName="unit"  fxFlex="1 1 100%" style="display: block; height: 38px; padding: 5px 8px; border: 1px solid #0000001f; border-radius: 4px;">
                </div>
                <!--<div fxLayout fxLayoutAlign="space-between" style="gap: 12px">-->
                    <div class="form-field">
                        <label>
                            Gauge Type <span class="required-asterisk">*</span>
                        </label>
                        <select matInput formControlName="type" style="display: block; height: 38px; padding: 5px 8px; border: 1px solid #0000001f; border-radius: 4px;" required>
                            <option value="RADIAL">Radial</option>
                            <option value="VBAR">Vertical Bar</option>
                            <option value="HBAR">Horizontal Bar</option>
                        </select>
                    </div>
                    <div class="form-field">
                        <label>
                            Threshold <span class="required-asterisk">*</span>
                        </label>
                        <input type="number" matInput formControlName="threshold" fxFlex="1 1 100%" style="display: block; height: 38px; padding: 5px 8px; border: 1px solid #0000001f; border-radius: 4px;" required>
                    </div>
                <!--</div>-->
                <div fxLayout fxLayoutAlign="space-between" style="gap: 12px">
                    <div class="form-field">
                        <label>
                            Node ID <span class="required-asterisk">*</span>
                        </label>
                        <input type="number" matInput formControlName="nodeId"  fxFlex="1 1 100%" style="display: block; height: 38px; padding: 5px 8px; border: 1px solid #0000001f; border-radius: 4px;" required>
                    </div>
                    <div class="form-field">
                        <label>
                            Pin Number <span class="required-asterisk">*</span>
                        </label>
                        <input type="number" matInput formControlName="pinNumber"  fxFlex="1 1 100%" style="display: block; height: 38px; padding: 5px 8px; border: 1px solid #0000001f; border-radius: 4px;" required>
                    </div>
                </div>
                <div fxLayout fxLayoutAlign="space-between" style="gap: 12px">
                    <div class="form-field">
                        <label>
                            Min <span class="required-asterisk">*</span>
                        </label>
                        <input type="number" matInput formControlName="min"  fxFlex="1 1 100%" style="display: block; height: 38px; padding: 5px 8px; border: 1px solid #0000001f; border-radius: 4px;" required>
                    </div>
                    <div class="form-field">
                        <label>
                            Max <span class="required-asterisk">*</span>
                        </label>
                        <input type="number" matInput formControlName="max" fxFlex="1 1 100%" style="display: block; height: 38px; padding: 5px 8px; border: 1px solid #0000001f; border-radius: 4px;" required>
                    </div>
                </div>
            </div>
            <div mat-dialog-actions fxLayout="row" fxLayoutAlign="end center">
                <button mat-button mat-raised-button color="primary" type="submit" [disabled]="(isLoading$ | async)">
                    Save
                </button>
            </div>
        </form>
        `;
        ctx.customDialog.customDialog(pinSettingsHtml, PinSettingsDialogController).subscribe();  
    };

    ctx.ngZone.run(function() {
        // console.log("settings", settings.analogPinsConfigName)
        const observer = new MutationObserver(function(mutationsList) {
            // console.log("mutations list", mutationsList);
            mutationsList.forEach(function(mutation, i) {
                mutation.addedNodes.forEach(function(addedNode) {
    				if(addedNode.classList.contains("pin-card")){
    				    var pinIndex = parseInt($(addedNode).data("pin-index"));
    				    var pin = $scope.pins[pinIndex];
    				    var {min, max, unit, type, threshold} = pin;
    				    var gaugeInstance;
    				    var pinCardGaugeContainer = $(addedNode).children(".pin-card-gaugeContainer");

    				    var radGaugeCanvasElement = pinCardGaugeContainer.children("canvas.pin-card-radialGauge");
    				    var radGaugeValueElement = pinCardGaugeContainer.children(".pin-card-radGaugeValUnit").children(".pin-card-radGaugeValue");
    				    var radGaugeUnitElement = pinCardGaugeContainer.children(".pin-card-radGaugeValUnit").children(".pin-card-radGaugeUnit");

    				    if(type === "RADIAL"){
    				        gaugeInstance = new Donut(radGaugeCanvasElement[0]).setOptions(radialGaugeOptions);

    				        gaugeInstance.setTextField(radGaugeValueElement[0])
    				        gaugeInstance.maxValue = max;
    				        gaugeInstance.setMinValue(min);
    				        gaugeInstance.setThreshold(threshold)
    				        gaugeInstance.set(min);
    				        radGaugeUnitElement.text(unit);
    				    }else{
    				        // Remove unit from radial gauge uint elements
    				        radGaugeUnitElement.hide();
    				        radGaugeCanvasElement.hide();

    				        var canvasElement = pinCardGaugeContainer.children("canvas.pin-card-barGauge")[0];
    				        // console.log("bar gauge canvas", canvasElement);
    				        gaugeInstance = new BarGauge(canvasElement, type === "VBAR",
    				        {
                                min: min,
                                max: max,
                                value: min,
                                unit: unit,
                                barFillColor: "rgba(219,219,219,0.4)",
                                barLength: type === "VBAR" ? 150 : 300,
                                barBreadth: 30,
                                textColor: "white",
                                threshold: threshold === undefined ? max - (0.8 * max) : threshold
                            });
    				    }
    				    $scope.pins[pinIndex].gaugeInstance = gaugeInstance;

    				    // console.log( "modded pin", $scope.pins[pinIndex]);
    				}
    				// observer.disconnect();
        		});
        	});
        });
        // console.log(ctx)
        var pinsListContainer = document.getElementById(pinsListContainerId);
        // console.log("pinslistcontainer", pinsListContainer);
        observer.observe(pinsListContainer, { subtree: false, childList: true });
        // console.log("uuid", entityId)
        if(ctx.defaultSubscription.targetDeviceId){
            ctx.attributeService.getEntityAttributes(entityId, "SERVER_SCOPE", [settings.analogPinsConfigName]).subscribe(function(data){
                // console.log("<----------Getting Attributes---------->");
                if (data.length < 1) return;
                // console.log("data", data);
                $scope.pins = data[0].value;

                if($scope.pins.length < 1) return;

                // $scope.pins.forEach(function(pinData){
                //     $scope.renderPin(pinData);
                // });
                createTelemetryKeysSubscription();
            });
        }
    });
};


self.onResize = function() {

};

self.onDestroy = function() {
};

