const handleDefaultLegend = function(){
    const legend = self.ctx.$containerParent.next("tb-legend");
    legend.hide();
};

const buildSeries = function(subscriptionData){
    if(subscriptionData.length < 1 ) return [];
    // Clone subscription data
    const clonedData = subscriptionData.map(function(dPoint){return dPoint})
    // Get index of series to be removed and used for annotations
    // NOTE: In case of multiple series being selected, only the first will be removed
    const seriesIndex = clonedData.findIndex(function({dataKey}){
        return dataKey.settings.useForAnnotations;
    });
    // console.log("series index to be removed", seriesIndex);

    if(seriesIndex >= 0) clonedData.splice(seriesIndex, 1);

    // console.log("total sub data after purge", clonedData.length, "data", clonedData);

    const series = clonedData.map(function({data, dataKey}){
        // Hide data key from series when 
        // if(dataKey.settings.useForAnnotations) return null;
        return {
            name: dataKey.label,
            data: data,
            color: dataKey.color
        };
    });// TODO: Change variable name

    // console.log("build series", series);
    return series;
};


self.onInit = function() {
    const {ctx} = self;
    const {timeWindow, settings, $container, stateController} = ctx;

    console.log(ctx);
    // Initial handlers (functions to handle dashboard workarounds, e.g. handling fullscreen mode)
    handleDefaultLegend();
    handleWidgetStyles();

    // Define DOM elements
    mainChartContainer = $container.find(".chartsContainer .mainChart");
    brushChartContainer = $container.find(".chartsContainer .brushChart");

    // Get settings
    chartType = settings.chartType;
    showMarkers = settings.showMarkers;
    showToolbar = settings.showToolbar;
    showLegend = settings.showLegend;
    curve = settings.curve;
    brushChartSettings = settings.brushChart;
    annotationsSettings = settings.annotations;
    telemetryKeysPrefixes = settings.telemetryKeysPrefixes;

    // Load static assets (images, svg, etc.)


    // Render initial UI
    renderInitUi();

    // 
    const widgetInDashboard = stateController && stateController.dashboardIdValue

    if(!widgetInDashboard) return;

    const {entityId, entityType} = self.ctx.defaultSubscription.datasources[0];

    fetchRoomNumber({id: entityId, entityType}).subscribe(function(result){
        if(result.length < 1) return;
        const roomNum = result[0].value;
        console.log({roomNum});
        // Create the subscriptions info array and subscribe to telemetry key(s)
        const timeseries = telemetryKeysPrefixes.map(function(key){
            return {
                name: key.prefix + "_" + roomNum,
                color: key.color,
                label: key.label
            };
        })
        console.log({timeseries})
        // const telemetryKey = telemetryKeysPrefixes[0].prefix + "_" + roomNum;
        const subscriptionsInfo = [{
            type: "entity",
            entityType,
            entityId,
            timeseries
        }];
        createCustomSubscription(subscriptionsInfo).subscribe(function(subscription){
            // console.log({subscription});
        });
    });
};

self.onDataUpdated = function() {
    // console.log("data updated",  self.ctx.data);
    const {ctx} = self;
    const {defaultSubscription} = ctx;
    const {data} = defaultSubscription;

    const series = buildSeries(data);

    if(series.length < 1) return;

    mainChart.updateSeries(series);
    brushChart.updateSeries(series);

    renderAnnotations(series);
};

// self.onLatestDataUpdated = function() {

