self.onResize = function() {
    // FIXME: When bruch chart has selection and height is changed, height of selection stays same

    const {height, $container} = self.ctx;
    const {heightThreshold, show: showBrushChart} = brushChartSettings;

    const widgetContainerHeight = $container.height();
    const xAxisHeight = 27.2;
    const brushChartHeight = brushChartContainer.height();

    // Hide/show brush chart depending on the set height threshold
    if(showBrushChart){
        if(height < heightThreshold){
            brushChartContainer.hide();
            // Update main chart height
            mainChart.updateOptions({
                chart: {
                    height: "100%"
                }
            });
        }else{
            brushChartContainer.show();
            // Update main chart height
            mainChart.updateOptions({
                chart: {
                    height: widgetContainerHeight - brushChartHeight
                }
            });
        }
    }
};

