                            index: 4,
                            title: 'Reset Zoom',
                            class: 'apexcharts-reset-icon',
                            click: function (chart, options, e) {
                                chart.resetSeries();

                                // TODO: Update selection option in brush chart to cover full time window
                                // brushChart.updateOptions({
                                //     chart: {
                                //         selection: {
                                //             xaxis: {
                                //                 min: timeWindow.minTime,
                                //                 max: timeWindow.maxTime
                                //             }
                                //         }
                                //     }
                                // });
                            }
                        }
                    ]
                },
                export:{
                    csv: {
                        filename: filename
                    },
                    svg: {
                        filename: filename
                    },
                    png: {
                        filename: filename
