          curve: curve,
          lineCap: "butt"
        },
        grid: {
            show: false
        },
        series: series,
        xaxis: {
            type: 'datetime',
            min: timeWindow.minTime,
            max: timeWindow.maxTime,
            labels: {
                datetimeFormatter: {
                    year: 'yyyy',
                    month: 'MMM \'yy',
                    day: 'dd MMM',
                    hour: 'HH:mm'
                },
                style: {
                    colors: axisColor,
                    fontFamily: fontFamily,
                }
            },
            tooltip: {
                enabled: false
            }
        },
        yaxis:{
            decimalsInFloat: 2,
            tickAmount: isStateYAxis ? 1 : 6,
            labels:{
                style: {
                    colors: axisColor,
                    fontFamily: fontFamily,
                },
