    chart.render();

    return chart;
};

const onDataUpdated = function(subscription){
    // console.log("onDataUpdated", {subscription});
    const {data} = subscription;
    const series = buildSeries(data);

    if(series.length < 1) return;

    mainChart.updateSeries(series);
    brushChart.updateSeries(series);

    renderAnnotations(series);
}

const onDataUpdateError = function(subscription, e){
    console.log("onDataUpdatedError", {subscription, e})
}

const createCustomSubscription = function(subscriptionsInfo){
    const {subscriptionApi, defaultSubscription} = self.ctx;

    const subscriptionOptions = {
        timeWindowConfig: defaultSubscription.timeWindowConfig,
        callbacks: {
            onDataUpdated: (subscription, detectChanges) => ctx.ngZone.run(() => {
                onDataUpdated(subscription, detectChanges);
            }),
            timeWindowUpdated: (subscription, timeWindowConfig) => ctx.ngZone.run(() => {
                console.log({timeWindowConfig})
            }),
            onDataUpdateError: (subscription, e) => ctx.ngZone.run(() =>{
                onDataUpdateError(subscription, e);
            }),
        }
    };

    return subscriptionApi.createSubscriptionFromInfo("timeseries", subscriptionsInfo, subscriptionOptions, false, true);
};

const fetchRoomNumber = function(entityId){
    return self.ctx.attributeService.getEntityAttributes(entityId, "SERVER_SCOPE", ["roomNum"]);
};
// Render initial UI elements
const renderInitUi = function(){
    const {defaultSubscription, timeWindow, dashboardWidget, $container, settings} = self.ctx;
    const {datasources, data} = defaultSubscription;

    // const chartFilename = dashboardWidget.title;
    const {widgetId} = dashboardWidget;

    const mainChartId = "main-chart-" + widgetId;
    const brushChartId = "brush-chart-" + widgetId;

    const {yAxis} = settings;
    const {type: yAxisType} = yAxis;

    const isStateYAxis = yAxisType === "state";

    if(data.length < 1) return;

    const series = buildSeries(data);

    mainChart = buildMainChart(mainChartId, series);

    renderAnnotations(series);

    // Hide/show brush chart depending on widget settings
    if(!brushChartSettings.show){
        brushChartContainer.hide();
        // Handle main chart height
        mainChart.updateOptions({
            chart: {
                height: "100%"
            }
        });
    }

    brushChart = buildBrushChart(brushChartId, mainChartId, series);
};

const renderAnnotations = function(series, chart){
    if(series.length < 1) return;

    let xAnnotations = [];
    let yAnnotations = [];

    const {disconnection, thresholdValue} = annotationsSettings;
    // Render disconnection annotations
    const {enable: enableDisconnectAnno} = disconnection;
    const {enable: enableThreholdAnno} = thresholdValue;

    // Render disconnection duration annotations if enabled
    if(enableDisconnectAnno){
        const {threshold, aLabel} = disconnection;
        const subscriptionData = self.ctx.defaultSubscription.data;
        // console.log("def sub data", subscriptionData, "series", series);
        const dPoint = subscriptionData.find(function({dataKey}){
            return dataKey.settings.useForAnnotations;
        });

        if(dPoint){
            // Get the series that will be used for annotations
            // const seriesIndex = series.findIndex(function({name}){
            //     return dPoint.dataKey.label === name;
            // });

            // const singleSeries = series[seriesIndex];
            // console.log("series", singleSeries, "data point", dPoint);
            // Annotations array with null values
            let prevTs;
            const nullableAnnotations = dPoint.data.map(function([ts, value]){
                // const [ts, value] = dPoint;
                let annotation = null;
                const seriesColor = dPoint.dataKey.color; // TODO: Change this after looping

                if(prevTs !== undefined && prevTs !== null){
                    // Duration between current & previous timestamps
                    const duration = ts - prevTs;

                    if(duration > threshold){
                        annotation =  {
                            x: prevTs,
                            x2: ts,
                            fillColor: seriesColor,
                            opacity: 0.1,
                            label:{
                                text: aLabel.text,
                                borderColor: seriesColor,
                                style:{
                                    background: seriesColor,
                                    color: txtColor,
                                    fontSize: aLabel.fontSize,
                                    fontFamily: fontFamily,
                                },
                                offsetX: 0,
                                offsetY: -6.4,
                            }
                        };
                    }
                }

                prevTs = ts;

                return annotation;
            });
            // Filter out null values
            const disconnectAnnotations = nullableAnnotations.filter(function(anno){return anno});

            // Add disconnection annotations
            xAnnotations = [...xAnnotations, ...disconnectAnnotations];
        }

    }

    // Render threshold value annotations if enabled
    if(enableThreholdAnno){
        const {value, aLabel, bgColor, textColor} = thresholdValue;

        const thresholdAnnotations = [
            {
                y: value,
                borderColor: bgColor,
                strokeDashArray: 4,
                label: {
                  borderColor: bgColor,

                  style: {
                    color: textColor,
                    background: bgColor,
                    fontSize: aLabel.fontSize,
                    fontFamily: fontFamily,
                  },
                  text: `${aLabel.text} ${value}`,
                }
            }
        ];

        yAnnotations = [...yAnnotations, ...thresholdAnnotations];
    }

    mainChart.updateOptions({
        annotations:{
            xaxis: xAnnotations,
            yaxis: yAnnotations
        }
    });
    // TODO: Render other types of annotations
};

const buildBrushChart = function(id, mainChartId, series){
    const {timeWindow} = self.ctx;
    const brushChartHeight = brushChartContainer.height();
    const isStateYAxis = self.ctx.settings.yAxis.type === "state";

    const brushChartOpts = {
        series: series,
        chart: {
            id: id,
            height: brushChartHeight,
            type: chartType,
            brush:{
                target: mainChartId,
                enabled: true
            },
            selection: {
                enabled: true,
                fill: {
                    color: chartSelectionColor,
                    opacity: 0.3
                }
            },
            background: widgetBgColor,
        },
        fill:{
            colors:"#858585",
            opacity: 0.3
        },
        legend:{
          show: false  
        },
        stroke: {
          curve: curve
        },
        fill: chartType === "area" ? {
            type: 'gradient',
            gradient: {
                opacityFrom: 0.91,
                opacityTo: 0.1,
            }
        } : {},
        xaxis: {
            type: 'datetime',
            labels:{
                show: brushChartSettings.showXAxisLabels,
                style: {
                    colors: axisColor,
                    fontFamily: fontFamily,
                }
            },
            // min: timeWindow.minTime,
            // max: timeWindow.maxTime,
        },
        yaxis: {
            tickAmount: isStateYAxis? 1 : 2,
            labels:{
                formatter: (value) => {
                    if(isStateYAxis) return yAxisMapper(value);
                    else return value.toFixed(2);
                },
                style: {
                    colors: axisColor,
                    fontFamily: fontFamily,
                },
            }
        },
        grid: {
            show: false
        }
    };

    chart = new ApexCharts(brushChartContainer[0], brushChartOpts);
    chart.render().then(function(data){
        // console.log("end of render", data)
    });

    return chart;
};

