.acControl {
    height: 100%;
    background-color: #333333;
    border-radius: 10px;
    font-family: "TT Mussels";
    font-weight: 700;
    color: #D9D9D9;
}

.acControl-header {
    padding: 10px 20px 10px 20px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
}

.acControl-titleContainer {
    display: flex;
    flex-direction: row;
    align-items: center;
}

.title {
    margin-left: 10px;
}

.acControl-switch {
    width: 51px;
    height: 31px;
    position: absolute;
    top: 5%;
    right: 7%;
}

.checkbox {
    opacity: 0;
    width: 0;
    height: 0;
    position: absolute;
}

.switch {
    width: 100%;
    height: 100%;
    display: block;
    background-color: #424142;
    border-radius: 16px;
    cursor: pointer;
    transition: all 0.2s ease-out;
}

.slider {
    width: 27px;
    height: 27px;
    position: absolute;
    left: calc(50% - 27px/2 - 10px);
    top: calc(50% - 27px/2);
    border-radius: 50%;
    background: #FFFFFF;
    box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.15), 0px 3px 1px rgba(0, 0, 0, 0.06);
    transition: all 0.2s ease-out;
    cursor: pointer;
}

.checkbox:checked+.switch {
    background-color: #00AA6C;
}

.checkbox:checked+.switch .slider {
    left: calc(50% - 27px/2 + 10px);
    top: calc(50% - 27px/2);
}

#tabs {
    left: 0;
    /*display: flex;*/
    /*flex-direction: column;*/
    /*align-items: flex-start*/
}

#acs {
    list-style-type: none;
    padding: 0;
}

.acButton {
    color: #D9D9D9;
    background-color: #333333;
    font-size: 20px;
    padding: 6px 40px 6px 8px;
    margin-bottom: 15px;
    vertical-align: middle;
    width: fit-content;
}

.selectedTab {
    color: #00AA6C;
    background-color: #424142;
    border-radius: 0px 25px 25px 0px;
}

#temperatureControl {
    background-color: #424142;
    border-radius: 30px;
    font-size: 25px;
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    width: 140px;
    align-items: center;
    margin-bottom: 12%;
}

#setTemp {
    margin: 10px;
    font-size: 22px;
}

#settings {
    display: flex;
    flex-direction: row;
    width: 52%;
    height: 70%;
    align-items: stretch;
    justify-content: space-around;
}

#settingsLabels {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: space-evenly;
    /*scale: 0.8;*/
}

#settingsIcons {
    height: 100%;
    width: 50%;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
}

.selected {
    fill: #00AA6C;
}

.selectedSwing {
    stroke: #00AA6C;
    fill: #00AA6C;
}

.middle {
    display: flex;
    flex-direction: row;
    /*background-color: red;*/
    height: 100%;
}

.fanTemp {
    display: flex;
    flex-direction: column;
}

#fanSection {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    /*scale: 1.1;*/
}

#tabContentContainer {
    width: 100%;
    height: 100%;
}

.tabContent {
    display: none;
}

.tabContent.active {
    display: flex;
    align-items: center;
    width: 100%;
    height: 80%;
    justify-content: space-evenly;
}

#sensorsContainer {
background-color: #424142;
    border-radius: 25px 0px 0px 25px;
    position: absolute;
    top: 24%;
    right: 0;
    display: inline-flex;
    font-weight: 900;
    justify-content: space-around;
    width: 45%;
    /* max-width: 100%; */
    min-width: 40%;
}

#mode,
#programs,
#swing {
    display: flex;
    align-items: center;
    justify-content: space-between;
    /*scale: 1.1;*/
}

#swing {
    display: flex;
    justify-content: flex-start;
}

#horizontalSwing {
    margin-left: 15%;
}

.sensor {
    display: flex;
    align-items: center;
    padding: 2%;
}

.value {
    font-size: 20px;
    font-weight: 900;
    padding: 3%;
}

.actual {
    font-size: 14px;
    /*padding: 5%;*/
    font-weight: 900;
}

.confirmationDialog {
    width: 100%;
    height: 60%;
    position: absolute;
    align-items: center;
    flex-direction: column;
    justify-content: center;
    visibility: hidden;
}

.dialogContent {
    width: 100%;
    display: flex;
    align-items: center;
    flex-direction: column;
}

.dialogButtons {
    display: flex;
    width: 100%;
    align-items: center;
    justify-content: center;
}

.dialogButton {
    all: unset;
    font-family: 'TT Mussels';
    background-color: #424142;
    padding: 1.5% 11% 1.5% 11%;
    border-radius: 37px;
    margin: 0 2% 0 2%;
    cursor: pointer;
}

.dialogText {
    width: 100%;
    margin-bottom: 10%;
    margin-left: 48%;
    font-size: larger;
    background-color: #424142;
    padding: 1% 0% 1% 3%;
    border-radius: 25px 0px 0px 25px;
}

#yes {
    color: #00AA6C;
}

#no {
    color: #F72119;
}
